mapper.update:
		UPDATE 
    		HOTEL
		SET

			HOTEL_NAME = #{newValue}


			HOTEL_TEL = #{newValue}


			HOTEL_AREA = #{newValue}


			HOTEL_ADDRESS = #{newValue}


			HOTEL_CONCEPT = #{newValue}

		WHERE
    		HOTEL_ID = #{hotelId}

		UPDATE 
    		ROOM
		SET

			ROOM_NAME = #{newValue}


			ROOM_PRICE = #{newValue}


			ROOM_AMOUNT = #{newValue}


			ROOM_STANDARD_PEOPLE = #{newValue}


			ROOM_MAX_PEOPLE = #{newValue}


			ROOM_PRICE_PEOPLE = #{newValue}


			ROOM_SERVICES = #{newValue}


			ROOM_INFO = #{newValue}


		WHERE
    		ROOM_ID = #{roomId}
